import stepOneCart from '@/views/main/cart/stepOneCart'
import stepTwoCart from '@/views/main/cart/stepTwoCart'
import stepThreeCart from '@/views/main/cart/stepThreeCart'
export default {
    data () {
        return {
            stepper: 1,
            steps: [
                {
                    label: 'Carrito',
                    completed: false,
                },
                {
                    label: 'Direccion',
                    completed: false,
                },
                {
                    label: 'Fecha entrega',
                    completed: false,
                },
            ],
            }
    },
    components:{
        stepOneCart,
        stepTwoCart,
        stepThreeCart
    },
    methods:{
        nextStep(step){
                this.stepper = step
                console.log('next')
        },
        finish(){
            if(this.$store.state.carrito.pedido.direction_id == null){
                this.$store.dispatch('insertDirection')
                .then(response =>{
                    if(response != null){
                        this.$store.dispatch('selectDirection',response.id)
                        .then(response1 =>{
                            if(response1){
                                this.$store.dispatch('updateProductAccoodingPedido')
                                  .then(response2 =>{
                                      console.log(response2)
                                      if(response2 != false){
                                        this.$store.dispatch('updatePedido',localStorage.pedido_id)
                                        .then(response3=>{
                                            if (response3 == true){
                                                location.reload()
                                            }
                                        })
                                      }
                                  })
                            }
                        })
                    }
                })
            }else{
                this.$store.dispatch('updateProductAccoodingPedido')
                .then(response2 =>{
                    console.log(response2)
                    if(response2 != false){
                        this.$store.dispatch('updatePedido',localStorage.pedido_id)
                        .then(response3=>{
                            if (response3 == true){
                                location.reload()
                            }
                        })
                    }
                })
            }
            Swal.fire({
                text: 'se ha confirmado tu pedido',
                type: 'success',
            })
            console.log('se realizo el pedido')
        },
    }
}
